


CREATE TABLE "LearningPackage" (
    "learningPackageId" INT PRIMARY KEY,
    "title" VARCHAR(255) NOT NULL,
    "description" TEXT NOT NULL,
    "category" VARCHAR(255) NOT NULL
);

CREATE TABLE "LearningFact" (
    "learningFactId" INT PRIMARY KEY,
    "title" VARCHAR(255) NOT NULL,
    "question" TEXT NOT NULL,
    "answer" TEXT NOT NULL,
    "nextStudyTime" DATE NOT NULL,
    "lastDateReview" DATE NOT NULL,
    "nbTimeReviewed" INT NOT NULL,
    "confidenceLevel" INT NOT NULL DEFAULT 0,
    "learningPackageId" INT NOT NULL,
    FOREIGN KEY ("learningPackageId") REFERENCES "LearningPackage"("learningPackageId")
);


-- run from here to the end

ALTER SEQUENCE public."LearningFact_learningFactId_seq"
    RESTART WITH 1000;
ALTER SEQUENCE public."LearningPackage_learningPackageId_seq"
    RESTART WITH 1000;

